drop table ldap_oc_mappings cascade; drop sequence ldap_oc_mappings_id_seq; create table ldap_oc_mappings ( id serial not null primary key, name varchar(64) not null, keytbl varchar(64) not null, keycol varchar(64) not null, create_proc varchar(255), delete_proc varchar(255), expect_return int not null );
drop table ldap_attr_mappings; drop sequence ldap_attr_mappings_id_seq; create table ldap_attr_mappings ( id serial not null primary key, oc_map_id integer not null references ldap_oc_mappings(id), name varchar(255) not null, sel_expr varchar(255) not null, sel_expr_u varchar(255), from_tbls varchar(255) not null, join_where varchar(255), add_proc varchar(255), delete_proc varchar(255), param_order int not null, expect_return int not null );
drop table ldap_entries; drop sequence ldap_entries_id_seq; create table ldap_entries ( id serial not null primary key, dn varchar(255) not null, oc_map_id integer not null references ldap_oc_mappings(id), parent int NOT NULL, keyval int NOT NULL, UNIQUE ( oc_map_id, keyval ), UNIQUE ( dn ) );
drop table ldap_entry_objclasses; create table ldap_entry_objclasses ( entry_id integer not null references ldap_entries(id), oc_name varchar(64) );
INSERT INTO ldap_oc_mappings(name,keytbl,keycol,expect_return) VALUES('inetOrgPerson','ldap_inetOrgPerson','id',0);
INSERT INTO ldap_oc_mappings(name,keytbl,keycol,expect_return) VALUES('organizationalUnit','ldap_organizationalUnit','id',0);
/* DROP VIEW ldap_inetOrgPerson CASCADE; */ CREATE OR REPLACE VIEW ldap_inetorgperson AS SELECT a.id, a.name as cn, a.phone AS telephonenumber, a.name AS sn, a.name AS givenname, a.fax AS facsimiletelephonenumber, a.mobile, a.phone AS homephone, lower(a.email) AS mail, a.street, a.zip AS postalcode FROM res_partner_address as a WHERE a.email IS NOT NULL AND a.type = 'contact';
DROP TABLE ldap_organizationalUnit; drop sequence ldap_organizationalUnit_id_seq; CREATE TABLE ldap_organizationalUnit ( id serial NOT NULL, "name" character(255), CONSTRAINT "ldap_organizationalUnit_pkey" PRIMARY KEY (id) );
INSERT INTO ldap_organizationalUnit(name) VALUES ('addressbook');
INSERT INTO ldap_attr_mappings(oc_map_id,name,sel_expr,from_tbls,join_where,param_order,expect_return) VALUES(1,'telephoneNumber','telephoneNumber','ldap_inetOrgPerson','1=1',3,0);
/* drop view ldap_entries;*/ CREATE OR REPLACE VIEW ldap_entries AS SELECT 0 AS id, 'ou=addressbook,dc=fbwgroup,dc=local' AS dn, 2 AS oc_map_id, 0 AS parent, 0 AS keyval UNION SELECT ldap_inetorgperson.id, ('cn=' || ldap_inetorgperson.cn) || ',ou=addressbook,dc=fbwgroup,dc=local' AS dn, 1 AS oc_map_id, 0 AS parent, ldap_inetorgperson.id AS keyval FROM ldap_inetorgperson;
